INSERT INTO `wp_rg_form_meta` VALUES (1,'{\"title\":\"Submit Article\",\"description\":\"\",\"labelPlacement\":\"top_label\",\"descriptionPlacement\":\"below\",\"button\":{\"type\":\"text\",\"text\":\"Submit\",\"imageUrl\":\"\",\"conditionalLogic\":{\"actionType\":\"hide\",\"logicType\":\"all\",\"rules\":[{\"fieldId\":\"8\",\"operator\":\"<\",\"value\":\"2\"}]}},\"fields\":[{\"type\":\"html\",\"id\":7,\"label\":\"\",\"adminLabel\":\"\",\"isRequired\":false,\"size\":\"medium\",\"errorMessage\":\"\",\"inputs\":null,\"displayOnly\":true,\"formId\":1,\"description\":\"\",\"allowsPrepopulate\":false,\"inputMask\":false,\"inputMaskValue\":\"\",\"inputType\":\"\",\"labelPlacement\":\"\",\"descriptionPlacement\":\"\",\"subLabelPlacement\":\"\",\"placeholder\":\"\",\"cssClass\":\"\",\"inputName\":\"\",\"adminOnly\":false,\"noDuplicates\":false,\"defaultValue\":\"\",\"choices\":\"\",\"conditionalLogic\":\"\",\"failed_validation\":\"\",\"productField\":\"\",\"multipleFiles\":false,\"maxFiles\":\"\",\"calculationFormula\":\"\",\"calculationRounding\":\"\",\"enableCalculation\":\"\",\"disableQuantity\":false,\"displayAllCategories\":false,\"useRichTextEditor\":false,\"content\":\"<p>We are a crowdsourced D-magazine!<\\/p>\\n<p>Everything you see here is written by the BACKFEED community and edited by its collective intelligence.\\nIf you want to learn more about how this works and why you should consider participating, please visit the <a href=\\\"\\/faq\\/\\\">FAQ page<\\/a><\\/p>\",\"pageNumber\":1},{\"type\":\"html\",\"id\":9,\"label\":\"\",\"adminLabel\":\"\",\"isRequired\":false,\"size\":\"medium\",\"errorMessage\":\"\",\"inputs\":null,\"displayOnly\":true,\"formId\":1,\"description\":\"\",\"allowsPrepopulate\":false,\"inputMask\":false,\"inputMaskValue\":\"\",\"inputType\":\"\",\"labelPlacement\":\"\",\"descriptionPlacement\":\"\",\"subLabelPlacement\":\"\",\"placeholder\":\"\",\"cssClass\":\"\",\"inputName\":\"\",\"adminOnly\":false,\"noDuplicates\":false,\"defaultValue\"